    }

    /**
     * Returns the number of related Locality objects.
     *
     * @param Criteria $criteria
     * @param boolean $distinct
     * @param PropelPDO $con
     * @return int             Count of related Locality objects.
     * @throws PropelException
     */
    public function countLocalitys(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
    {
        $partial = $this->collLocalitysPartial && !$this->isNew();
        if (null === $this->collLocalitys || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collLocalitys) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getLocalitys());
                }
                $query = LocalityQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByArea($this)
                    ->count($con);
            }
        } else {
            return count($this->collLocalitys);
        }
    }

    /**
     * Method called to associate a Locality object to this object
     * through the Locality foreign key attribute.
     *
     * @param    Locality $l Locality
     * @return Area The current object (for fluent API support)
     */
    public function addLocality(Locality $l)
    {
        if ($this->collLocalitys === null) {
            $this->initLocalitys();
            $this->collLocalitysPartial = true;
        }
        if (!$this->collLocalitys->contains($l)) { // only add it if the **same** object is not already associated
            $this->doAddLocality($l);
        }

        return $this;
    }

    /**
     * @param	Locality $locality The locality object to add.
     */
    protected function doAddLocality($locality)
    {
        $this->collLocalitys[]= $locality;
        $locality->setArea($this);
    }

    /**
     * @param	Locality $locality The locality object to remove.
     */
    public function removeLocality($locality)
    {
        if ($this->getLocalitys()->contains($locality)) {
            $this->collLocalitys->remove($this->collLocalitys->search($locality));
            if (null === $this->localitysScheduledForDeletion) {
                $this->localitysScheduledForDeletion = clone $this->collLocalitys;
                $this->localitysScheduledForDeletion->clear();
            }
            $this->localitysScheduledForDeletion[]= $locality;
            $locality->setArea(null);
        }
    }


    /**
     * If this collection has already been initialized with
     * an identical criteria, it returns the collection.
     * Otherwise if this Area is new, it will return
     * an empty collection; or if this Area has previously
     * been saved, it will retrieve related Localitys from storage.
     *
     * This method is protected by default in order to keep the public
     * api reasonable.  You can provide public methods for those you
     * actually need in Area.
     *
     * @param Criteria $criteria optional Criteria object to narrow the query
     * @param PropelPDO $con optional connection object
     * @param string $join_behavior optional join type to use (defaults to Criteria::LEFT_JOIN)
     * @return PropelObjectCollection|Locality[] List of Locality objects
     */
    public function getLocalitysJoinsfGuardUser($criteria = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
    {
        $query = LocalityQuery::create(null, $criteria);
        $query->joinWith('sfGuardUser', $join_behavior);

        return $this->getLocalitys($query, $con);
    }

    /**
     * Clears the current object and sets all attributes to their default values
     */
    public function clear()
    {
        $this->id = null;
        $this->is_active = null;
        $this->name = null;
        $this->latitude = null;
        $this->longitude = null;
        $this->viewport_sw_lt = null;
        $this->viewport_sw_ln = null;
        $this->viewport_ne_lt = null;
        $this->viewport_ne_ln = null;
        $this->created_at = null;
        $this->updated_at = null;
        $this->alreadyInSave = false;
        $this->alreadyInValidation = false;
        $this->clearAllReferences();
        $this->applyDefaultValues();
        $this->resetModified();
        $this->setNew(true);
        $this->setDeleted(false);
    }

    /**
     * Resets all references to other model objects or collections of model objects.
     *
     * This method is a user-space workaround for PHP's inability to garbage collect
     * objects with circular references (even in PHP 5.3). This is currently necessary
     * when using Propel in certain daemon or large-volumne/high-memory operations.
     *
     * @param boolean $deep Whether to also clear the references on all referrer objects.
     */
    public function clearAllReferences($deep = false)
    {
        if ($deep) {
            if ($this->collLocalitys) {
                foreach ($this->collLocalitys as $o) {
                    $o->clearAllReferences($deep);
                }
            }
        } // if ($deep)

        if ($this->collLocalitys instanceof PropelCollection) {
            $this->collLocalitys->clearIterator();
        }
        $this->collLocalitys = null;
    }

    /**
     * return the string representation of this object
     *
     * @return string
     */
    public function __toString()
    {
        return (string) $this->exportTo(AreaPeer::DEFAULT_STRING_FORMAT);
    }

    /**
     * return true is the object is in saving state
     *
     * @return boolean
     */
    public function isAlreadyInSave()
    {
        return $this->alreadyInSave;
    }

    // Timestampable behavior

    /**
     * Mark the current object so that the update date doesn't get updated during next save
     *
     * @return     Area The current object (for fluent API support)
     */
    public function keepUpdateDateUnchanged()
    {
        $this->modifiedColumns[] = AreaPeer::UPDATED_AT;

        return $this;
    }

    /**
     * Catches calls to virtual methods
     */
    public function __call($name, $params)
    {

        // symfony_behaviors behavior
        if ($callable = sfMixer::getCallable('BaseArea:' . $name))
        {
          array_unshift($params, $this);
          return call_user_func_array($callable, $params);
        }


        return parent::__call($name, $params);
    }

}
