     *
     * @return  Arrival The current object (for fluent API support)
     */
    public function populateFromVersion($version, $con = null, &$loadedObjects = array())
    {

        $loadedObjects['Arrival'][$version->getId()][$version->getVersion()] = $this;
        $this->setId($version->getId());
        $this->setBookingId($version->getBookingId());
        $this->setDay($version->getDay());
