        $this->setFlight($version->getFlight());
        $this->setRateCost($version->getRateCost());
        $this->setCalculatedCost($version->getCalculatedCost());
        $this->setRateName($version->getRateName());
        $this->setNote($version->getNote());
        $this->setPaymentMethodId($version->getPaymentMethodId());
        $this->setLocalityFrom($version->getLocalityFrom());
        $this->setLocalityTo($version->getLocalityTo());
        $this->setDriverId($version->getDriverId());
        $this->setVehicleId($version->getVehicleId());
        $this->setCancelled($version->getCancelled());
        $this->setCreatedAt($version->getCreatedAt());
        $this->setUpdatedAt($version->getUpdatedAt());
        $this->setVersion($version->getVersion());
        $this->setVersionCreatedAt($version->getVersionCreatedAt());
        $this->setVersionCreatedBy($version->getVersionCreatedBy());
        if ($fkValue = $version->getBookingId()) {
            if (isset($loadedObjects['Booking']) && isset($loadedObjects['Booking'][$fkValue]) && isset($loadedObjects['Booking'][$fkValue][$version->getBookingIdVersion()])) {
                $related = $loadedObjects['Booking'][$fkValue][$version->getBookingIdVersion()];
            } else {
                $related = new Booking();
                $relatedVersion = BookingVersionQuery::create()
                    ->filterById($fkValue)
                    ->filterByVersion($version->getBookingIdVersion())
                    ->findOne($con);
                $related->populateFromVersion($relatedVersion, $con, $loadedObjects);
                $related->setNew(false);
            }
            $this->setBooking($related);
        }

        return $this;
    }

