    /**
     * Retrieve object using using composite pkey values.
     * @param   int $id
     * @param   int $version
     * @param      PropelPDO $con
     * @return   ArrivalVersion
     */
    public static function retrieveByPK($id, $version, PropelPDO $con = null) {
        $_instancePoolKey = serialize(array((string) $id, (string) $version));
         if (null !== ($obj = ArrivalVersionPeer::getInstanceFromPool($_instancePoolKey))) {
             return $obj;
        }

        if ($con === null) {
            $con = Propel::getConnection(ArrivalVersionPeer::DATABASE_NAME, Propel::CONNECTION_READ);
        }
        $criteria = new Criteria(ArrivalVersionPeer::DATABASE_NAME);
        $criteria->add(ArrivalVersionPeer::ID, $id);
        $criteria->add(ArrivalVersionPeer::VERSION, $version);
        $v = ArrivalVersionPeer::doSelect($criteria, $con);

        return !empty($v) ? $v[0] : null;
    }
