        if ($this->collDepartures instanceof PropelCollection) {
            $this->collDepartures->clearIterator();
        }
        $this->collDepartures = null;
        if ($this->collBookingVersions instanceof PropelCollection) {
            $this->collBookingVersions->clearIterator();
        }
        $this->collBookingVersions = null;
        $this->aCustomer = null;
        $this->aVehicleType = null;
    }

