        return $this->alreadyInSave;
    }

    // Timestampable behavior

    /**
     * Mark the current object so that the update date doesn't get updated during next save
     *
     * @return     Booking The current object (for fluent API support)
     */
    public function keepUpdateDateUnchanged()
    {
        $this->modifiedColumns[] = BookingPeer::UPDATED_AT;

        return $this;
    }

    // versionable behavior

    /**
     * Enforce a new Version of this object upon next save.
     *
     * @return Booking
     */
    public function enforceVersioning()
    {
        $this->enforceVersion = true;

        return $this;
    }

    /**
     * Checks whether the current state must be recorded as a version
     *
     * @param PropelPDO $con An optional PropelPDO connection to use.
     *
     * @return  boolean
     */
    public function isVersioningNecessary($con = null)
    {
        if ($this->alreadyInSave) {
            return false;
        }

        if ($this->enforceVersion) {
            return true;
        }

        if (BookingPeer::isVersioningEnabled() && ($this->isNew() || $this->isModified() || $this->isDeleted())) {
            return true;
        }
      // to avoid infinite loops, emulate in save
      $this->alreadyInSave = true;
        foreach ($this->getArrivals(null, $con) as $relatedObject) {
            if ($relatedObject->isVersioningNecessary($con)) {
          $this->alreadyInSave = false;

                return true;
            }
        }
      $this->alreadyInSave = false;

      // to avoid infinite loops, emulate in save
      $this->alreadyInSave = true;
        foreach ($this->getDepartures(null, $con) as $relatedObject) {
            if ($relatedObject->isVersioningNecessary($con)) {
          $this->alreadyInSave = false;

                return true;
            }
        }
      $this->alreadyInSave = false;


        return false;
    }

    /**
     * Creates a version of the current object and saves it.
     *
     * @param   PropelPDO $con the connection to use
     *
     * @return  BookingVersion A version object
     */
    public function addVersion($con = null)
    {
        $this->enforceVersion = false;

        $version = new BookingVersion();
        $version->setId($this->getId());
        $version->setBookingDate($this->getBookingDate());
        $version->setYear($this->getYear());
        $version->setNumber($this->getNumber());
        $version->setAdult($this->getAdult());
        $version->setReduced($this->getReduced());
        $version->setChild($this->getChild());
        $version->setContact($this->getContact());
        $version->setRifFile($this->getRifFile());
        $version->setCustomerId($this->getCustomerId());
        $version->setVehicleTypeId($this->getVehicleTypeId());
        $version->setIsConfirmed($this->getIsConfirmed());
        $version->setCreatedAt($this->getCreatedAt());
        $version->setUpdatedAt($this->getUpdatedAt());
        $version->setVersion($this->getVersion());
        $version->setVersionCreatedAt($this->getVersionCreatedAt());
        $version->setVersionCreatedBy($this->getVersionCreatedBy());
        $version->setBooking($this);
        if ($relateds = $this->getArrivals($con)->toKeyValue('Id', 'Version')) {
            $version->setArrivalIds(array_keys($relateds));
            $version->setArrivalVersions(array_values($relateds));
        }
        if ($relateds = $this->getDepartures($con)->toKeyValue('Id', 'Version')) {
            $version->setDepartureIds(array_keys($relateds));
            $version->setDepartureVersions(array_values($relateds));
        }
        $version->save($con);

        return $version;
    }

    /**
     * Sets the properties of the curent object to the value they had at a specific version
     *
     * @param   integer $versionNumber The version number to read
     * @param   PropelPDO $con the connection to use
     *
     * @return  Booking The current object (for fluent API support)
     * @throws  PropelException - if no object with the given version can be found.
     */
    public function toVersion($versionNumber, $con = null)
    {
        $version = $this->getOneVersion($versionNumber, $con);
        if (!$version) {
            throw new PropelException(sprintf('No Booking object found with version %d', $version));
        }
        $this->populateFromVersion($version, $con);

        return $this;
    }

    /**
     * Sets the properties of the curent object to the value they had at a specific version
     *
     * @param   BookingVersion $version The version object to use
     * @param   PropelPDO $con the connection to use
     * @param   array $loadedObjects objects thats been loaded in a chain of populateFromVersion calls on referrer or fk objects.
     *
     * @return  Booking The current object (for fluent API support)
     */
    public function populateFromVersion($version, $con = null, &$loadedObjects = array())
    {

        $loadedObjects['Booking'][$version->getId()][$version->getVersion()] = $this;
        $this->setId($version->getId());
        $this->setBookingDate($version->getBookingDate());
        $this->setYear($version->getYear());
        $this->setNumber($version->getNumber());
        $this->setAdult($version->getAdult());
        $this->setReduced($version->getReduced());
        $this->setChild($version->getChild());
        $this->setContact($version->getContact());
        $this->setRifFile($version->getRifFile());
        $this->setCustomerId($version->getCustomerId());
        $this->setVehicleTypeId($version->getVehicleTypeId());
        $this->setIsConfirmed($version->getIsConfirmed());
        $this->setCreatedAt($version->getCreatedAt());
        $this->setUpdatedAt($version->getUpdatedAt());
        $this->setVersion($version->getVersion());
        $this->setVersionCreatedAt($version->getVersionCreatedAt());
        $this->setVersionCreatedBy($version->getVersionCreatedBy());
        if ($fkValues = $version->getArrivalIds()) {
            $this->clearArrivals();
            $fkVersions = $version->getArrivalVersions();
            $query = ArrivalVersionQuery::create();
            foreach ($fkValues as $key => $value) {
                $c1 = $query->getNewCriterion(ArrivalVersionPeer::ID, $value);
                $c2 = $query->getNewCriterion(ArrivalVersionPeer::VERSION, $fkVersions[$key]);
                $c1->addAnd($c2);
                $query->addOr($c1);
            }
            foreach ($query->find($con) as $relatedVersion) {
                if (isset($loadedObjects['Arrival']) && isset($loadedObjects['Arrival'][$relatedVersion->getId()]) && isset($loadedObjects['Arrival'][$relatedVersion->getId()][$relatedVersion->getVersion()])) {
                    $related = $loadedObjects['Arrival'][$relatedVersion->getId()][$relatedVersion->getVersion()];
                } else {
                    $related = new Arrival();
                    $related->populateFromVersion($relatedVersion, $con, $loadedObjects);
                    $related->setNew(false);
                }
                $this->addArrival($related);
            }

            $this->resetPartialArrivals(false);
        }
        if ($fkValues = $version->getDepartureIds()) {
            $this->clearDepartures();
            $fkVersions = $version->getDepartureVersions();
            $query = DepartureVersionQuery::create();
            foreach ($fkValues as $key => $value) {
                $c1 = $query->getNewCriterion(DepartureVersionPeer::ID, $value);
                $c2 = $query->getNewCriterion(DepartureVersionPeer::VERSION, $fkVersions[$key]);
                $c1->addAnd($c2);
                $query->addOr($c1);
            }
            foreach ($query->find($con) as $relatedVersion) {
                if (isset($loadedObjects['Departure']) && isset($loadedObjects['Departure'][$relatedVersion->getId()]) && isset($loadedObjects['Departure'][$relatedVersion->getId()][$relatedVersion->getVersion()])) {
                    $related = $loadedObjects['Departure'][$relatedVersion->getId()][$relatedVersion->getVersion()];
                } else {
                    $related = new Departure();
                    $related->populateFromVersion($relatedVersion, $con, $loadedObjects);
                    $related->setNew(false);
                }
                $this->addDeparture($related);
            }

            $this->resetPartialDepartures(false);
        }

        return $this;
    }

    /**
     * Gets the latest persisted version number for the current object
     *
     * @param   PropelPDO $con the connection to use
     *
     * @return  integer
     */
    public function getLastVersionNumber($con = null)
    {
        $v = BookingVersionQuery::create()
            ->filterByBooking($this)
            ->orderByVersion('desc')
            ->findOne($con);
        if (!$v) {
            return 0;
        }

        return $v->getVersion();
    }

    /**
     * Checks whether the current object is the latest one
     *
     * @param   PropelPDO $con the connection to use
     *
     * @return  boolean
     */
    public function isLastVersion($con = null)
    {
        return $this->getLastVersionNumber($con) == $this->getVersion();
    }

    /**
     * Retrieves a version object for this entity and a version number
     *
     * @param   integer $versionNumber The version number to read
     * @param   PropelPDO $con the connection to use
     *
     * @return  BookingVersion A version object
     */
    public function getOneVersion($versionNumber, $con = null)
    {
        return BookingVersionQuery::create()
            ->filterByBooking($this)
            ->filterByVersion($versionNumber)
            ->findOne($con);
    }

    /**
     * Gets all the versions of this object, in incremental order
     *
     * @param   PropelPDO $con the connection to use
