            // way of knowing (without running a query) what objects should be invalidated
            // from the cache based on this Criteria.
            CustomerRateExtraPeer::clearInstancePool();
            // rename for clarity
            $criteria = clone $values;
        } elseif ($values instanceof CustomerRateExtra) { // it's a model object
            // invalidate the cache for this single object
            CustomerRateExtraPeer::removeInstanceFromPool($values);
            // create criteria based on pk values
            $criteria = $values->buildPkeyCriteria();
        } else { // it's a primary key, or an array of pks
            $criteria = new Criteria(CustomerRateExtraPeer::DATABASE_NAME);
            // primary key is composite; we therefore, expect
            // the primary key passed to be an array of pkey values
            if (count($values) == count($values, COUNT_RECURSIVE)) {
                // array is not multi-dimensional
                $values = array($values);
            }
            foreach ($values as $value) {
                $criterion = $criteria->getNewCriterion(CustomerRateExtraPeer::CUSTOMER_ID, $value[0]);
                $criterion->addAnd($criteria->getNewCriterion(CustomerRateExtraPeer::RATE_EXTRA_ID, $value[1]));
                $criteria->addOr($criterion);
                // we can invalidate the cache for this single PK
                CustomerRateExtraPeer::removeInstanceFromPool($value);
            }
        }

        // Set the correct dbName
        $criteria->setDbName(CustomerRateExtraPeer::DATABASE_NAME);

        $affectedRows = 0; // initialize var to track total num of affected rows

        try {
            // use transaction because $criteria could contain info
            // for more than one table or we could emulating ON DELETE CASCADE, etc.
            $con->beginTransaction();

            $affectedRows += BasePeer::doDelete($criteria, $con);
            CustomerRateExtraPeer::clearRelatedInstancePool();
            $con->commit();

            return $affectedRows;
        } catch (PropelException $e) {
            $con->rollBack();
            throw $e;
        }
    }

    /**
     * Validates all modified columns of given CustomerRateExtra object.
     * If parameter $columns is either a single column name or an array of column names
     * than only those columns are validated.
     *
     * NOTICE: This does not apply to primary or foreign keys for now.
     *
     * @param      CustomerRateExtra $obj The object to validate.
     * @param      mixed $cols Column name or array of column names.
     *
     * @return mixed TRUE if all columns are valid or the error message of the first invalid column.
     */
    public static function doValidate($obj, $cols = null)
    {
        $columns = array();

        if ($cols) {
            $dbMap = Propel::getDatabaseMap(CustomerRateExtraPeer::DATABASE_NAME);
            $tableMap = $dbMap->getTable(CustomerRateExtraPeer::TABLE_NAME);

            if (! is_array($cols)) {
                $cols = array($cols);
            }

            foreach ($cols as $colName) {
                if ($tableMap->hasColumn($colName)) {
                    $get = 'get' . $tableMap->getColumn($colName)->getPhpName();
                    $columns[$colName] = $obj->$get();
                }
            }
        } else {

        }

        return BasePeer::doValidate(CustomerRateExtraPeer::DATABASE_NAME, CustomerRateExtraPeer::TABLE_NAME, $columns);
    }

    /**
     * Retrieve object using using composite pkey values.
     * @param   int $customer_id
     * @param   int $rate_extra_id
     * @param      PropelPDO $con
     * @return   CustomerRateExtra
     */
    public static function retrieveByPK($customer_id, $rate_extra_id, PropelPDO $con = null) {
        $_instancePoolKey = serialize(array((string) $customer_id, (string) $rate_extra_id));
         if (null !== ($obj = CustomerRateExtraPeer::getInstanceFromPool($_instancePoolKey))) {
             return $obj;
        }

        if ($con === null) {
            $con = Propel::getConnection(CustomerRateExtraPeer::DATABASE_NAME, Propel::CONNECTION_READ);
        }
        $criteria = new Criteria(CustomerRateExtraPeer::DATABASE_NAME);
        $criteria->add(CustomerRateExtraPeer::CUSTOMER_ID, $customer_id);
        $criteria->add(CustomerRateExtraPeer::RATE_EXTRA_ID, $rate_extra_id);
        $v = CustomerRateExtraPeer::doSelect($criteria, $con);

        return !empty($v) ? $v[0] : null;
    }
