     * @return Route The current object (for fluent API support)
     * @throws PropelException
     */
    public function setLocalityRelatedByLocalityFrom(Locality $v = null)
    {
        if ($v === null) {
            $this->setLocalityFrom(NULL);
        } else {
            $this->setLocalityFrom($v->getId());
        }

        $this->aLocalityRelatedByLocalityFrom = $v;

        // Add binding for other direction of this n:n relationship.
        // If this object has already been added to the Locality object, it will not be re-added.
        if ($v !== null) {
            $v->addRouteRelatedByLocalityFrom($this);
        }


        return $this;
    }


